import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editedTitle, setEditedTitle] = useState("");
  const [hoveredId, setHoveredId] = useState(null);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    const res = await axios.get("/api/todos");
    setTodos(res.data);
  };

  const createTodo = async () => {
    if (!title.trim()) return;
    await axios.post("/api/todos", { title });
    setTitle("");
    fetchTodos();
  };

  const updateTodo = async (id, data) => {
    await axios.put(`/api/todos/${id}`, data);
    fetchTodos();
  };

  const deleteTodo = async (id) => {
    await axios.delete(`/api/todos/${id}`);
    fetchTodos();
  };

  const handleTitleUpdate = async (id) => {
    if (editedTitle.trim()) {
      await updateTodo(id, { title: editedTitle });
    }
    setEditingId(null);
    setEditedTitle("");
  };

  return (
    <div className="App">
      <h1>To-Do List</h1>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Add task..."
      />
      <button className="btn-update" onClick={createTodo}>
        Add
      </button>

      <ul>
        {todos.map((todo) => (
          <li key={todo._id}>
            {/* Complete Checkbox */}
            <input
              type="checkbox"
              checked={todo.completed}
              onChange={() =>
                updateTodo(todo._id, { completed: !todo.completed })
              }
              title="Mark as complete"
            />

            {/* Editable or display title */}
            {editingId === todo._id ? (
              <>
                <input
                  className="editing-input"
                  value={editedTitle}
                  onChange={(e) => setEditedTitle(e.target.value)}
                  placeholder="Edit title"
                />
                <button
                  className="btn-update"
                  onClick={() => handleTitleUpdate(todo._id)}
                >
                  Update
                </button>
                <button
                  className="btn-cancel"
                  onClick={() => setEditingId(null)}
                >
                  Cancel
                </button>
              </>
            ) : (
              <span
                className={`todo-text ${todo.completed ? "completed" : ""} ${
                  todo.pinned ? "pinned" : ""
                }`}
              >
                {todo.title}
              </span>
            )}

            {/* Edit and Delete buttons */}
            {editingId !== todo._id && (
              <button
                className="btn-edit"
                onClick={() => {
                  setEditingId(todo._id);
                  setEditedTitle(todo.title);
                }}
              >
                Edit
              </button>
            )}

            <button className="btn-delete" onClick={() => deleteTodo(todo._id)}>
              Delete
            </button>

            {/* Optional Pin Checkbox */}
            <label style={{ marginLeft: "8px" }}>
              📌
              <input
                type="checkbox"
                checked={todo.pinned}
                onChange={() => updateTodo(todo._id, { pinned: !todo.pinned })}
                title="Pin this task"
              />
            </label>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
