import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");
  const [hoveredId, setHoveredId] = useState(null);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      const res = await axios.get("/api/todos");
      setTodos(res.data);
    } catch (err) {
      console.error("Error fetching todos:", err);
    }
  };

  const createTodo = async () => {
    if (!title.trim()) return;
    try {
      await axios.post("/api/todos", { title });
      setTitle("");
      fetchTodos();
    } catch (err) {
      console.error("Error creating todo:", err);
    }
  };

  const updateTodo = async (id, data) => {
    try {
      await axios.put(`/api/todos/${id}`, data);
      fetchTodos();
    } catch (err) {
      console.error("Error updating todo:", err);
    }
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`/api/todos/${id}`);
      fetchTodos();
    } catch (err) {
      console.error("Error deleting todo:", err);
    }
  };
  const handleUpdate = async (id) => {
    if (!editedTitle.trim()) return;
    await updateTodo(id, { title: editedTitle });
    setEditingId(null);
    setEditedTitle("");
  };

  return (
    <div className="App">
      <h1>📝 To-Do List</h1>
      <div className="input-section">
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Add a task..."
        />
        <button onClick={createTodo}>Add</button>
      </div>

      <ul className="todo-list">
        {todos.map((todo) => (
          <li
            key={todo._id}
            className={`todo-item ${todo.pinned ? "pinned" : ""}`}
            onMouseEnter={() => setHoveredId(todo._id)}
            onMouseLeave={() => setHoveredId(null)}
          >
            <input
              type="checkbox"
              checked={todo.completed}
              onChange={() =>
                updateTodo(todo._id, { completed: !todo.completed })
              }
            />
            {editingId === todo._id ? (
              <>
                <input
                  value={editedTitle}
                  onChange={(e) => setEditedTitle(e.target.value)}
                  className="edit-input"
                />
                <button
                  className="btn-update"
                  onClick={() => handleUpdate(todo._id)}
                >
                  ✅
                </button>
                <button
                  className="btn-cancel"
                  onClick={() => setEditingId(null)}
                >
                  ❌
                </button>
              </>
            ) : (
              <>
                <span
                  className={`todo-text ${todo.completed ? "completed" : ""}`}
                  style={{ flex: 1 }}
                >
                  {todo.title}
                </span>
                <button
                  className="btn-edit"
                  onClick={() => {
                    setEditingId(todo._id);
                    setEditedTitle(todo.title);
                  }}
                >
                  ✏️
                </button>
              </>
            )}

            {hoveredId === todo._id && (
              <button
                className="btn-pin-hover"
                onClick={() => updateTodo(todo._id, { pinned: !todo.pinned })}
                title={todo.pinned ? "Unpin" : "Pin"}
              >
                📌
              </button>
            )}

            <button
              className="btn-delete"
              onClick={() => deleteTodo(todo._id)}
              title="Delete"
            >
              ❌
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
