import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editedTitle, setEditedTitle] = useState("");

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    const res = await axios.get("/api/todos");
    setTodos(res.data);
  };

  const createTodo = async () => {
    if (!title.trim()) return;
    await axios.post("/api/todos", { title });
    setTitle("");
    fetchTodos();
  };

  const updateTodo = async (id, data) => {
    await axios.put(`/api/todos/${id}`, data);
    fetchTodos();
  };

  const deleteTodo = async (id) => {
    await axios.delete(`/api/todos/${id}`);
    fetchTodos();
  };

  const handleTitleUpdate = async (id) => {
    if (editedTitle.trim()) {
      await updateTodo(id, { title: editedTitle });
    }
    setEditingId(null);
    setEditedTitle("");
  };

  return (
    <div className="App">
      <h1>To-Do List</h1>
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Add task..."
      />
      <button onClick={createTodo}>Add</button>
      <ul>
        {todos.map((todo) => (
          <li key={todo._id}>
            {editingId === todo._id ? (
              <>
                <input
                  value={editedTitle}
                  onChange={(e) => setEditedTitle(e.target.value)}
                  placeholder="Edit title"
                />
                <button onClick={() => handleTitleUpdate(todo._id)}>
                  Update
                </button>
                <button onClick={() => setEditingId(null)}>Cancel</button>
              </>
            ) : (
              <>
                <span
                  style={{
                    textDecoration: todo.completed ? "line-through" : "none",
                    fontWeight: todo.pinned ? "bold" : "normal",
                    marginRight: "10px",
                  }}
                >
                  {todo.title}
                </span>
                <button
                  onClick={() => {
                    setEditingId(todo._id);
                    setEditedTitle(todo.title);
                  }}
                >
                  Edit
                </button>
              </>
            )}
            <button
              onClick={() =>
                updateTodo(todo._id, { completed: !todo.completed })
              }
            >
              {todo.completed ? "Undo" : "Complete"}
            </button>
            <button
              onClick={() => updateTodo(todo._id, { pinned: !todo.pinned })}
            >
              {todo.pinned ? "Unpin" : "Pin"}
            </button>
            <button onClick={() => deleteTodo(todo._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
